pragma solidity ^0.4.10;
import './ERC20GardenToken.sol';
import './Ownable.sol';
import './SmartTokenInterface.sol';

/*
    Open issues:
    - possibly implement ERC223 standard
*/

/*
    Smart Token v0.1
*/
contract SmartToken is ERC20GardenToken, Ownable, SmartTokenInterface {
    string public version = '0.1';
    uint8 public numDecimalUnits = 0;       // for display purposes only
    bool public transfersEnabled = true;    // true if transfer/transferFrom are enabled, false if not
    address public changer = 0x0;           // changer contract address
    mapping(address => bool) public participationOf; //map of historic participations of spenders

    // triggered when a token changer is updated/removed
    event ChangerUpdate(address _prevChanger, address _newChanger);

    /*
        _name               token name
        _symbol             token short symbol, 1-6 characters
        _numDecimalUnits    for display purposes only
    */
    function SmartToken(string _name, string _symbol, uint8 _numDecimalUnits)
    ERC20GardenToken(_name, _symbol)
    {
        require(bytes(_name).length != 0 && bytes(_symbol).length >= 1 && bytes(_symbol).length <= 6); // validate input
        numDecimalUnits = _numDecimalUnits;
    }

    // verifies that a value is greater than zero
    modifier validAmount(uint256 _value) {
        require(_value > 0);
        _;
    }

    // allows execution only when transfers aren't disabled
    modifier transfersAllowed {
        assert(transfersEnabled);
        _;
    }

    // allows execution by the controller or changer
    modifier controllerOnly {
        // Important: this allows transfers been performed by external contracts too (!!!)
        // as long as tx is initiated by the owner
        assert(tx.origin == owner || msg.sender == changer);
        _;
    }

    /*
        sets the number of display decimal units
        can only be called by the token owner

        _numDecimalUnits    new number of decimal units
    */
    function setNumDecimalUnits(uint8 _numDecimalUnits) public onlyOwner {
        numDecimalUnits = _numDecimalUnits;
    }

    /*
        disables/enables transfers
        can only be called by the token owner (if no changer is defined) or the changer contract (if a changer is defined)

        _disable    true to disable transfers, false to enable them
    */
    function disableTransfers(bool _disable) public controllerOnly {
        transfersEnabled = !_disable;
    }

    /*
        increases the token supply and sends the new tokens to an account
        can only be called by the token owner (if no changer is defined) or the changer contract (if a changer is defined)

        _to         account to receive the new amount
        _amount     amount to increase the supply by
    */
    function issue(address _to, uint256 _amount)
    public
    controllerOnly
    validAddress(_to)
    validAmount(_amount)
    {
        require(_to != address(this)); // validate input
        totalSupply = safeAdd(totalSupply, _amount);
        balanceOf[_to] = safeAdd(balanceOf[_to], _amount);
        Transfer(this, _to, _amount);
    }

    /*
        removes tokens from an account and decreases the token supply
        can only be called by the token owner (if no changer is defined) or the changer contract (if a changer is defined)

        _from       account to remove the new amount from
        _amount     amount to decrease the supply by
    */
    function destroy(address _from, uint256 _amount)
    public
    controllerOnly
    validAmount(_amount)
    {
        balanceOf[_from] = safeSub(balanceOf[_from], _amount);
        totalSupply = safeSub(totalSupply, _amount);
        Transfer(_from, this, _amount);
    }

    /*
        sets a changer contract address
        can only be called by the token owner (if no changer is defined) or the changer contract (if a changer is defined)
        the changer can be set to null to transfer ownership from the changer to the owner

        _changer    new changer contract address (can also be set to 0x0 to remove the current changer)
    */
    function setChanger(address _changer) public controllerOnly {
        require(_changer != changer);
        address prevChanger = changer;
        changer = _changer;
        ChangerUpdate(prevChanger, changer);
    }

    // ERC20 standard method overrides with some extra functionality

    // send coins
    function transfer(address _to, uint256 _value) public transfersAllowed returns (bool success) {
        assert(super.transfer(_to, _value));
        keepParticipation(msg.sender);
        return true;
    }

    // an account/contract attempts to get the coins
    function transferFrom(address _from, address _to, uint256 _value) public controllerOnly transfersAllowed returns (bool success) {
        assert(super.transferFrom(_from, _to, _value));
        keepParticipation(_from);
        return true;
    }

    function keepParticipation(address _participant) private {
        if (balanceOf[_participant] == 0){
            //down to zero, but we store his participation
            participationOf[_participant] = true;
        }

    }

    // fallback
    function() {
        assert(false);
    }
}
